<link defer rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/splidejs/4.1.4/css/splide.min.css"
    integrity="sha512-KhFXpe+VJEu5HYbJyKQs9VvwGB+jQepqb4ZnlhUF/jQGxYJcjdxOTf6cr445hOc791FFLs18DKVpfrQnONOB1g=="
    crossorigin="anonymous" referrerpolicy="no-referrer" />
<script>
document.addEventListener("DOMContentLoaded", function() {
    const arrows = document.querySelectorAll(".Needle");

    arrows.forEach(arrow => {
        animateArrow(arrow, -180, parseInt(arrow.getAttribute("data-value")));
        observeDataValueChanges(arrow);
    });

    var InnerSlide = new Splide('.InnerSlide', {
        type: 'fade',
        rewind: true,
        autoplay: true,
        interval: 5000,
        perPage: 1,
        drag: false,
        pagination: true,
        arrows: false,
        start: 0,
    });

    var InnerSlide2 = new Splide('.InnerSlide2', {
        type: 'fade',
        rewind: true,
        autoplay: true,
        interval: 5000,
        perPage: 1,
        drag: false,
        pagination: true,
        arrows: false,
        start: 0,
    });

    var InnerSlide3 = new Splide('.InnerSlide3', {
        type: 'fade',
        rewind: true,
        autoplay: true,
        interval: 5000,
        perPage: 1,
        drag: false,
        pagination: true,
        arrows: false,
        start: 0,
    });

    var InnerSlide4 = new Splide('.InnerSlide4', {
        type: 'fade',
        rewind: true,
        autoplay: true,
        interval: 5000,
        perPage: 1,
        drag: false,
        pagination: true,
        arrows: false,
        start: 0,
    });

    var InnerSlide5 = new Splide('.InnerSlide5', {
        type: 'fade',
        rewind: true,
        autoplay: true,
        interval: 5000,
        perPage: 1,
        drag: false,
        pagination: true,
        arrows: false,
        start: 0,
    });

    var InnerSlide6 = new Splide('.InnerSlide6', {
        type: 'fade',
        rewind: true,
        autoplay: true,
        interval: 5000,
        perPage: 1,
        drag: false,
        pagination: true,
        arrows: false,
        start: 0,
    });

    InnerSlide.mount();
    InnerSlide2.mount();
    InnerSlide3.mount();
    InnerSlide4.mount();
    InnerSlide5.mount();
    InnerSlide6.mount();

    function animateArrow(arrow, initialValue, finalValue) {
        const duration = 500; // milliseconds
        const startTime = performance.now();

        function updateAnimation(currentTime) {
            const elapsedTime = currentTime - startTime;
            const progress = Math.min(elapsedTime / duration, 1);
            const interpolatedValue = initialValue + (finalValue - initialValue) * progress;

            const angle = -180 + (interpolatedValue / 100) * 180;
            arrow.style.transform = `rotate(${angle}deg)`;

            if (progress < 1) {
                requestAnimationFrame(updateAnimation);
            }
        }

        requestAnimationFrame(updateAnimation);
    }

    function observeDataValueChanges(arrow) {
        const observer = new MutationObserver(mutationsList => {
            mutationsList.forEach(mutation => {
                if (mutation.attributeName === "data-value") {
                    const finalValue = parseInt(mutation.target.getAttribute("data-value"));
                    const initialValue = parseInt(mutation.oldValue);
                    animateArrow(arrow, initialValue, finalValue);
                }
            });
        });

        observer.observe(arrow, {
            attributes: true,
            attributeOldValue: true
        });
    }
});
</script>
